from base64 import b64decode
from itertools import combinations
from collections import Counter

# 计算汉明距离,输入为两个等长的bytes
def hamming_distance(bytes1, bytes2):
    distance = 0
    for byte1, byte2 in zip(bytes1,bytes2):
        byte = byte1^byte2
        distance += sum(bit == '1' for bit in bin(byte))
    return distance

# 根据最小归一化汉明距离来确定密钥长度
def get_keylen(cipher):
    keylen_distance_pairs = []   # 用于记录[密钥长度,该密钥长度对应的平均归一化汉明长度]
    for keylen in range(2, 40+1):
        blocks = [cipher[i:i+keylen] for i in range(0, len(cipher), keylen)]
        hamming_distances = [hamming_distance(blocks[i],blocks[j])/keylen for i,j in combinations(range(len(blocks)),2)]
        keylen_distance_pairs.append([keylen, sum(hamming_distances)/len(hamming_distances)])
    keylen = min(keylen_distance_pairs, key=lambda x: x[1])[0]
    return keylen

# 得到密钥长度keylen后,将cipher按keylen分组后,分别取每组的同一位置的字节作为新的组
def key_byte_block(cipher, keylen):
    return [cipher[i::keylen] for i in range(keylen)]

def single_byte_key_XOR_block(single_byte_key, block):
    return bytes(single_byte_key^i for i in block)

# score English text based on letter frequency, 输入为字符串
def english_score(text):
    letter_frequency = {
        'a': .08167, 'b': .01492, 'c': .02782, 'd': .04253,
        'e': .12702, 'f': .02228, 'g': .02015, 'h': .06094,
        'i': .06094, 'j': .00153, 'k': .00772, 'l': .04025,
        'm': .02406, 'n': .06749, 'o': .07507, 'p': .01929,
        'q': .00095, 'r': .05987, 's': .06327, 't': .09056,
        'u': .02758, 'v': .00978, 'w': .02360, 'x': .00150,
        'y': .01974, 'z': .00074, ' ': .15000
    }
    score = sum([letter_frequency.get(chr(i), 0) for i in text.lower()])
    return score

# find the best key by trying all possible single-byte keys
def get_key(cipher, keylen):
    # best_score = 0
    # best_single_byte_key = None
    key = ''
    key_byte_blocks = key_byte_block(cipher, keylen)
    for i in range(keylen):
        single_key_score_pairs = []
        for single_byte_key in range(0xFF+1):
            decrypted_bytes = single_byte_key_XOR_block(single_byte_key, key_byte_blocks[i])
            score = english_score(decrypted_bytes)
            single_key_score_pairs.append([single_byte_key, score])
        best = max(single_key_score_pairs, key=lambda x: x[1])
        key += chr(best[0])
    return key

if __name__ == '__main__':
    # 题目给出cipher的base64编码,先进行base64解码
    cipher_base64 = ''
    cipher = b64decode(cipher_base64)  # base64解码,得字节串
    keylen = get_keylen(cipher)
    print('keylen:', keylen)
    key = get_key(cipher, keylen)
    print('key:', key)
    plain = ''
    for i in range(0, len(cipher)):
        plain += chr(ord(key[i % keylen]) ^ cipher[i])
    print('plain:',plain)






